    <form className="content">
      <ul className="shoppingColum">
        <li>お買い上げ商品</li>
        <li>数量</li>
        <li>売り場</li>
      </ul>
      <ul className="topic">
        {shopping.map((e, ind) => (
          <li key={ind} className="formData">
            <input
              name="food-name"
              type="text"
