                setList(food);
              }}
              required
            />
            <input
              name="quantity"
              type="number"
              min={0}
              onChange={(e) => {
                const food = [...list];
                food[ind]["quantity"] = e.target.value;
                setList(food);
              }}
              required
            />
            <select
              name="quantity-unit"
              onChange={(e) => {
                const food = [...list];
                food[ind]["quantity_unit"] = e.target.value;
                setList(food);
              }}
            >
              <option value="個/本/玉">個/本/玉</option>
              <option value="パック/袋/缶">パック/袋/缶</option>
              <option value="L">L</option>
              <option value="Kg">Kg</option>
            </select>
            <input
              name="expiration-date"
              type="date"
              onChange={(e) => {
                const food = [...list];
                food[ind]["expiration-date"] = e.target.value;
                setList(food);
              }}
            />
          </li>
        ))}
      </ul>
