import React, { useContext, useRef, useState } from "react";
import { VariableContext } from "../App";
import "./SignUp.css";

const SignUp = () => {
  const [, , , , setSignUpFlag] = useContext(VariableContext);
  const [inputData, setInputData] = useState({
    "user-id": "",
    "first-name": "",
    "last-name": "",
    password: "",
  });
  const htmlRef = useRef<HTMLDivElement>(null);

  const submitInputData = async () => {
    const response = await fetch("http://localhost:3333/signUp", {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify(inputData),
    }).then((e) => e.json());
    console.log("$$$$$$$$$$$$$", response);
    setSignUpFlag(false);
    alert("Sign up completed !!");
  };

  return (
    <div
      className="popUpBack"
      ref={htmlRef}
      onClick={(e) => {
        e.target === htmlRef.current && setSignUpFlag(false);
      }}
    >
      <section>
        <h1>Sign Up</h1>
        {/* user-id */}
        <input
          type="number"
          name="user-id"
          placeholder="User Id (numbers only)"
          value={inputData["user-id"]}
          required
          onChange={(e) =>
            setInputData({ ...inputData, [e.target.name]: e.target.value })
          }
        />
        {/* first-name */}
        <input
          type="text"
          name="first-name"
          placeholder="First name"
          value={inputData["first-name"]}
          required
          onChange={(e) =>
            setInputData({ ...inputData, [e.target.name]: e.target.value })
          }
        />
        {/* last-name */}
        <input
          type="text"
          name="last-name"
          placeholder="Last name"
          value={inputData["last-name"]}
          required
          onChange={(e) =>
            setInputData({ ...inputData, [e.target.name]: e.target.value })
          }
        />
        {/* password */}
        <input
          type="password"
          name="password"
          placeholder="Password"
          value={inputData.password}
          required
          onChange={(e) =>
            setInputData({ ...inputData, [e.target.name]: e.target.value })
          }
        />
        {/* ボタン */}
        <div className="buttonArea">
          <button onClick={submitInputData}>SUBMIT</button>
          <button
            onClick={() => {
              setSignUpFlag(false);
            }}
          >
            CLOSE
          </button>
        </div>
      </section>
    </div>
  );
};

export default SignUp;
