import React from "react";
import "./List.css";

type props = {
  postedArray: {
    id: number;
    title: string;
    "post-date": string;
    tag: string;
    url: string;
    pict: string;
    "p-user-id": number;
  }[];
  setPopup: Function;
};

const List = ({ postedArray, setPopup }: props) => {
  return (
    <>
      {postedArray.map((e, ind) => (
        <article key={ind}>
          {/* カード表 */}
          <div className="front">
            <figure>
              <img
                src={`./images/${"溺れる人のピクトグラム"}.svg`}
                alt="articleIcon"
              />
            </figure>
            <div className="content">
              <table>
                <thead>
                  <tr>
                    <th colSpan={2}>{e.title}</th>
                  </tr>
                </thead>
                <tbody>
                  <tr>
                    <th>posted</th>
                    <td>{e["post-date"]}</td>
                  </tr>
                  <tr>
                    <th>tag</th>
                    <td>{e["tag"]}</td>
                  </tr>
                  <tr>
                    <th>zamas</th>
                    <td>{e["p-user-id"]}</td>
                  </tr>
                </tbody>
              </table>
            </div>
          </div>

          {/* カード裏 */}
          <div className="back">
            <h3>review comment</h3>
            <section>
              <ul>
                <li>よかった〜ザマス</li>
                <li>最高ザマス</li>
              </ul>
              <a href="https://qiita.com/FumioNonaka/items/feb2fd5b362f2558acfa">
                <button>link</button>
              </a>
            </section>
          </div>
        </article>
      ))}
    </>
  );
};

export default List;
