import { ChangeEvent, useContext, useState } from "react";
import "./Login.css";
import React from "react";
import { VariableContext } from "../App";

const Login = () => {
  const [, , setUserData, , setSignUpFlag] = useContext(VariableContext);
  const initialValues = { "user-id": "", password: "" };
  const [formValues, setFormValues] = useState(initialValues);

  const handleChange = (e: ChangeEvent<HTMLInputElement>) => {
    const { name, value } = e.target;
    setFormValues({ ...formValues, [name]: value });
  };

  const handleSubmit = (e: { preventDefault: () => void }) => {
    e.preventDefault();
    const getData = async () => {
      const fetchData = await fetch("http://localhost:3333/user", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify(formValues),
      })
        .then((e) => e.json())
        .catch((reason) => {
          return false;
        });
      console.log(fetchData);

      if (fetchData) {
        setUserData(fetchData);
      } else {
        alert("ユーザIDかパスワードが間違えています");
      }
    };
    getData();
  };

  return (
    <div className="formContainer">
      <div className="formBox">
        <form onSubmit={handleSubmit}>
          <h1>KSK ZAMAS</h1>
          <hr />
          <div className="uiForm">
            <div className="formField">
              <label>USER ID</label>
              <input
                type="text"
                pattern="^[0-9]+$"
                minLength={4}
                maxLength={15}
                name="user-id"
                placeholder="UserId (Only Numbers)"
                value={formValues["user-id"]}
                onChange={(e) => handleChange(e)}
                required
              />
            </div>
            <div className="formField">
              <label>PASSWORD</label>
              <input
                type="password"
                pattern="^[a-zA-Z0-9]+$"
                minLength={4}
                maxLength={15}
                name="password"
                placeholder="Password"
                value={formValues.password}
                required
                autoComplete="on"
                onChange={(e) => handleChange(e)}
              />
            </div>
            <button className="submitButton">LOGIN</button>
          </div>
        </form>
        <button
          onClick={() => {
            setSignUpFlag(true);
          }}
          className="signUpButton"
        >
          SIGN UP
        </button>
      </div>
    </div>
  );
};
//a
export default Login;
