import React, { useState, useContext } from "react";
import "./List.css";
import { VariableContext } from "../App";
const List = () => {
  const [, , list, setList, userData, setUserData] =
    useContext(VariableContext);

  const handleRemoveItem = (index: number) => {
    const newList = [...list];
    newList.splice(index, 1);
    setList(newList);
  };

  const handleCompletedItem = (index: any) => {
    const newList = list.map(
      (item: { isCompleted: boolean }, itemIndex: any) => {
        if (itemIndex === index) {
          item.isCompleted = !item.isCompleted;
        }
        return item;
      }
    );
    setList(newList);
  };

  const handleUpdateItem = (index: any, value: any) => {
    const newList = list.map((item: { task: any }, itemIndex: any) => {
      if (itemIndex === index) {
        item.task = value;
      }
      return item;
    });
    setList(newList);
  };

  return (
    <form className="content">
      <ul className="shoppingColum">
        <li>買う物</li>
        <li>数量</li>
        <li>売り場</li>
      </ul>
      <ul className="topic">
        {list.map((e: any, index: React.Key | null | undefined) => (
          <li key={index} className="formData">
            <input
              type="checkbox"
              checked={list.isCompleted}
              onChange={() => handleCompletedItem(index)}
            />
            <input
              name="item"
              type="text"
              disabled={list.isCompleted}
              value={`${list.item}`}
              onchange={(e: { target: { value: any } }) =>
                handleUpdateItem(index, e.target.value)
              }
            />
            {/* <input
              name="quantity"
              type="number"
              min={0}
              onChange={(e) => {
                const food = [...list];
                food[index]["quantity"] = e.target.value;
                setList(food);
              }}
              required
            />
            <select
              name="quantity_unit"
              onChange={(e) => {
                const food = [...list];
                food[index]["quantity_unit"] = e.target.value;
                setList(food);
              }}
            >
              <option value="個/本/玉">個/本/玉</option>
              <option value="パック/袋/缶">パック/袋/缶</option>
              <option value="L">L</option>
              <option value="Kg">Kg</option>
            </select>
            <input
              name="login_id"
              type="number"
              onChange={(e) => {
                const food = [...list];
                food[index]["login_id"] = e.target.value;
                setList(food);
              }}
            /> */}
            <button onClick={() => handleRemoveItem(index)}>削除</button>
          </li>
        ))}
      </ul>
    </form>
  );
};

export default List;
