import React, { useContext, useEffect, useState } from "react";
import { VariableContext } from "../App";
import "./NewCreateShop.css";

export const NewCreateShop = () => {
  const [
    newCreateFlag,
    setNewCreateFlag,
    ,
    ,
    userData,
    setUserData,
    shop,
    setShop,
    shopTable,
  ] = useContext(VariableContext);

  const [tem, setTem] = useState([
    { shop_name: "カネスエ", corner_name: "野菜", directions: 1 },
    { shop_name: "カネスエ", corner_name: "肉", directions: 1 },
    { shop_name: "カネスエ", corner_name: "魚", directions: 1 },
    { shop_name: "カネスエ", corner_name: "乳製品", directions: 1 },
  ]);

  const handleUpdateItem = (e: any, index: any) => {
    const { name, value } = e.target;
    const newLists = tem.map((row: any, itemIndex) => {
      if (itemIndex === index) {
        console.log(name, value);
        row[name] = value;
      }
      return row;
    });
    setTem(newLists);
  };

  return (
    <form className="content">
      {/* <button onClick={(e) => handleRemoveItem(index)}>登録</button>
      <button onClick={(e) => handleRemoveItem(index)}>戻る</button> */}
      <ul className="shoppingColum">
        <li>お店</li>
        <li>売り場</li>
        <li>順番</li>
      </ul>
      <ul className="topic">
        {tem.map((row: any, index: number) => (
          <li key={index} className="formData">
            <input
              name="shop_name"
              type="text"
              value={`${row.shop_name}`}
              onChange={(e) => handleUpdateItem(e, index)}
              disabled
            />
            <select
              name="corner_name"
              value={`${row.corner_name}`}
              onChange={(e) => handleUpdateItem(e, index)}
            >
              <option value="野菜">野菜</option>
              <option value="肉">肉</option>
              <option value="魚">魚</option>
              <option value="乳製品">乳製品</option>
            </select>
            <select
              name="directions"
              value={`${row.directions}`}
              onChange={(e) => handleUpdateItem(e, index)}
            >
              <option value="1">1</option>
              <option value="2">2</option>
              <option value="3">3</option>
              <option value="4">4</option>
            </select>
          </li>
        ))}
      </ul>
    </form>
  );
};
