import React, { useState, useContext } from "react";
import "./List.css";
import { VariableContext } from "../App";
const List = () => {
  const initialArray = [...Array(1)];
  const [shopping, setShopping] = useState(initialArray);
  // eslint-disable-next-line react-hooks/rules-of-hooks
  const [, , list, setList, userData, setUserData] =
    useContext(VariableContext);

  const handleRemoveTask = (index) => {
    const newTodos = [...todos];
    newTodos.splice(index, 1);
    setTodos(newTodos);
  };

  const handleCompletedTask = (index) => {
    const newTodos = todos.map((todo, todoIndex) => {
      if (todoIndex === index) {
        todo.isCompleted = !todo.isCompleted;
      }
      return todo;
    });
    setTodos(newTodos);
  };

  const handleUpdateTask = (index, value) => {
    const newTodos = todos.map((todo, todoIndex) => {
      if (todoIndex === index) {
        todo.task = value;
      }
      return todo;
    });
    setTodos(newTodos);
  };

  return (
    <form className="content">
      <ul className="shoppingColum">
        <li>買う物</li>
        <li>数量</li>
        <li>売り場</li>
      </ul>
      <ul className="topic">
        {list.map((e, index) => (
          <li key={index} className="formData">
            <input
              type="checkbox"
              checked={list.isCompleted}
              onChange={() => handleCompletedTask(index)}
            />
            <input
              name="item"
              type="text"
              disabled={list.isCompleted}
              value={`${list.item}`}
              onchange={(e) => handleUpdateTask(index, e.target.value)}
            />
