                setList(food);
              }}
              required
            />
            <select
              name="quantity_unit"
              onChange={(e) => {
                const food = [...list];
                food[index]["quantity_unit"] = e.target.value;
                setList(food);
              }}
            >
              <option value="個/本/玉">個/本/玉</option>
              <option value="パック/袋/缶">パック/袋/缶</option>
              <option value="L">L</option>
              <option value="Kg">Kg</option>
            </select>
            <input
              name="login_id"
              type="number"
              onChange={(e) => {
                const food = [...list];
                food[index]["login_id"] = e.target.value;
                setList(food);
              }}
            /> */}
            <button onClick={() => handleRemoveTask(index)}>削除</button>
          </li>
