import { useEffect, useRef, useState } from "react";
import "./App.css";
import "./Reset.css";
import Header from "./components/Header";
import Modal from "./components/Modal";
import SearchList from "./components/SearchList";
import { table } from "./globals";

function App() {
  // valueは全てのテーブル情報を格納
  const [value, setValue] = useState<table[]>([]);
  const [tagList, setTagList] = useState<string[]>([]);
  const [imgList, setImgList] = useState<string[]>([
    "css",
    "express",
    "html",
    "javascript",
    "knex",
    "nodejs",
    "postgresql",
    "postman",
    "typescript",
    "git",
    "react",
  ]);
  const [filterValue, setFilterValue] = useState<table[]>([]);

  useEffect(() => {
    (async () => {
      const fetchData = await fetch("http://localhost:8000/list");
      const jsonData = await fetchData.json();
      //html nodelistはforEachでしか回せないなぜなら戻り値がないので元の形を変えない :(useStateの使い方が分からなかった,react,react 公式ドキュメント,2023-05-01)
      jsonData.forEach((element: any) => {
        const result = `${element.title},${element.tag},${element.keyword},${element.postedDate}`;
        //Allキーを新たに追加しそこにカンマで繋いだ上の文字列を小文字にしたものを格納　:(All: "usestateの使い方が分からなかった,react,react 公式ドキュメント,2023-05-01")
        element.All = result.toLowerCase();
      });
      setValue(jsonData);
    })();
  }, []);

  useEffect(() => {
    //tagを小文字にして配列に格納:(5) ['react', 'postgresql', 'git', 'typescript', 'treact, express']
    const arrMap = value.map((elem) => {
      return elem.tag.toLowerCase();
    });
    //タグの配列から重複を削除したsetオブジェクトを作成: Set(5) {'react', 'postgresql', 'git', 'typescript', 'treact, express'}
    const set = new Set(arrMap);
    //タグリストを更新
    setTagList(["All", ...Array.from(set)]);
    console.log("////////////", tagList);

    value.forEach((element: any) => {
      const result = `${element.title},${element.tag},${element.keyword},${element.postedDate}`;
      element.All = result.toLowerCase();
    });

    setFilterValue(value);
  }, [value]);

  const [show, setShow] = useState<boolean>(false);
  const openModal = () => {
    setShow(true);
  };

  const inputRef = useRef(null);

  return (
    <div className="App">
      <Modal
        show={show}
        setShow={setShow}
        tagList={tagList}
        inputRef={inputRef}
        setValue={setValue}
        value={value}
      />
      <Header
        tagList={tagList}
        setTagList={setTagList}
        openModal={openModal}
        setFilterValue={setFilterValue}
        value={value}
      />
      <SearchList filterValue={filterValue} imgList={imgList} />
    </div>
  );
}

export default App;
