import React, { useContext, useState } from "react";
import { VariableContext } from "../App";

export const Add = () => {
  const [inputNewItem, setInputNewItem] = useState("");
  const [, , list, setList, userData, setUserData] =
    useContext(VariableContext);
  const handleInput = (e: { target: { value: string } }) => {
    setInputNewItem(() => e.target.value);
  };

  const handleSubmit = (e: { preventDefault: () => void }) => {
    //ページのリロード処理を無効化
    e.preventDefault();
    if (inputNewItem === "") return;
    setList((list: any) => [
      ...list,
      { task: inputNewItem, isCompleted: false },
    ]);
    setInputNewItem("");
  };

  return (
    <>
      <form onSubmit={handleSubmit}>
        <input
          value={inputNewItem}
          placeholder="新しく買う物"
          onChange={handleInput}
        />
        <button type="submit">買う物を追加する!</button>
      </form>
    </>
  );
};
