import { useEffect, useState } from "react";
import "./App.css";
import Header from "./components/Header";
import List from "./components/List";
import Login from "./components/Login";
import React from "react";
import SignUp from "./components/SignUp";

type props = [
  value: {
    id: number;
    title: string;
    "post-date": string;
    tag: string;
    url: string;
    pict: string;
    "p-user-id": number;
  }[],
  userData: {
    "user-id": number;
    "first-name": string;
    "last-name": string;
    password: string;
  }[],
  setUserData: Function,
  signUpFlag: boolean,
  setSignUpFlag: Function
];

export const VariableContext = React.createContext<props>([
  [],
  [],
  () => {},
  false,
  () => {},
]);

function App() {
  // valueは全てのテーブル情報を格納
  const [value, setValue] = useState([]);
  const [userData, setUserData] = useState([]);
  const [signUpFlag, setSignUpFlag] = useState(false);

  useEffect(() => {
    const getData = async () => {
      const mainData = await fetch("http://localhost:3333/posted").then((e) =>
        e.json()
      );
      setValue(mainData);
    };
    getData();
  }, []);
  console.log("//////////////////////", userData);

  return (
    <>
      <VariableContext.Provider
        value={[value, userData, setUserData, signUpFlag, setSignUpFlag]}
      >
        {userData.length > 0 ? (
          <>
            <Header />
            <List />
          </>
        ) : (
          <>
            {signUpFlag && <SignUp />}
            <Login />
          </>
        )}
      </VariableContext.Provider>
    </>
  );
}

export default App;
