import React, { useContext, useState } from "react";
import { VariableContext } from "../App";

export const Add = () => {
  const [inputNewItem, setInputNewItem] = useState({ item: "" });
  const [, , , setLists, userData, setUserData] = useContext(VariableContext);

  const handleChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    const { name, value } = e.target;
    setInputNewItem({ ...inputNewItem, [name]: value });
  };

  const handleSubmit = (e: { preventDefault: () => void }) => {
    //ページのリロード処理を無効化
    e.preventDefault();
    if (!inputNewItem) return;
    setLists((lists: any) => [
      ...lists,
      { item: inputNewItem, isCompleted: false },
    ]);
    setInputNewItem("");
  };

  return (
    <>
      <form onSubmit={handleSubmit}>
        <input
          type="text"
          name="item"
          value={inputNewItem.item}
          placeholder="新しく買う物"
          onChange={(e) => handleChange(e)}
          required
        />
        {/* <input
          name="quantity"
          type="number"
          min={0}
          onChange={(e) => {
            const food = [...lists];
            food[index]["quantity"] = e.target.value;
            setLists(food);
          }}
          required
        /> */}
        <button type="submit">追加!</button>
      </form>
    </>
  );
};
