import React, { useEffect, useState } from "react";
import "./App.css";
import Header from "./components/Header";
import List from "./components/List";
import Modal from "./components/Modal";
import Login from "./components/Login";

type props = [
  popup: boolean,
  setPopup: React.Dispatch<React.SetStateAction<boolean>>,
  postedArray: {
    id: number;
    title: string;
    "post-date": string;
    tag: string;
    url: string;
    pict: string;
    "p-user-id": number;
  }[],
  setPostedArray: React.Dispatch<
    React.SetStateAction<
      {
        id: number;
        title: string;
        "post-date": string;
        tag: string;
        url: string;
        pict: string;
        "p-user-id": number;
      }[]
    >
  >
];
export const VariableContext = React.createContext<props>([
  false,
  () => {},
  [],
  () => {},
]);

export default function App() {
  const [postedArray, setPostedArray] = useState([
    {
      id: 0,
      title: "",
      "post-date": "",
      tag: "",
      url: "",
      pict: "",
      "p-user-id": 0,
    },
  ]);
  const [popup, setPopup] = useState(false);
  const [userData, setUserData] = useState("");

  useEffect(() => {
    const getData = async () => {
      const data = await fetch("http://localhost:3333/posted").then((e) =>
        e.json()
      );
      setPostedArray(data);
    };
    const kako = async () => {
      return "dummy";
    };
    getData();
  }, []);

  return (
    <>
      <VariableContext.Provider
        value={[popup, setPopup, postedArray, setPostedArray]}
      >
        {userData ? (
          <>
            <Header />
            <main>
              {popup && <Modal />}
              <List postedArray={postedArray} setPopup={setPopup} />
            </main>
          </>
        ) : (
          <Login userData={userData} setUserData={setUserData} />
        )}
      </VariableContext.Provider>
    </>
  );
}
