import { ChangeEvent, useEffect, useState } from "react";
import "./Login.css";
// interface errorsType {
//   username: string;
//   password: string;
// }{}

const Login = () => {
  const initialValues = { username: "", password: "" };
  const [formValues, setFormValues] = useState(initialValues);
  //   const [formErros, setFormErrors] = useState<errorsType>({});
  const [isSubmit, setIsSubmit] = useState<boolean>(false);

  const handleChange = (e: ChangeEvent<HTMLInputElement>) => {
    // console.log(e.target.name);
    const { name, value } = e.target;
    setFormValues({ ...formValues, [name]: value });
    // console.log(formValues);
  };

  const handleSubmit = (e: { preventDefault: () => void }) => {
    e.preventDefault();
    // setFormErrors(validate(formValues));
    setIsSubmit(true);
  };

  useEffect(() => {
    console.log(formErros);
    //エラーなしでかつ送信されているなら。
    if (Object.keys(formErros).length === 0 && isSubmit) {
      console.log("////////", formValues);
    } else {
    }
  }, [formErros]);

  //   //バリデーションチェック
  //   const validate = (values: { username: string; password: string }) => {
  //     const errors = { username: "", password: "" };
  //     //valueが空ならerrrosの配列に入れる。
  //     if (!values.username) {
  //       errors.username = "ユーザー名を入力してください。";
  //     }
  //     if (!values.password) {
  //       errors.password = "パスワードを入力してください。";
  //     } else if (values.password.length < 4) {
  //       errors.password = "4文字以上15文字以下のパスワードを入力してください";
  //     } else if (values.password.length > 15) {
  //       errors.password = "4文字以上15文字以下のパスワードを入力してください";
  //     }
  //     return errors;
  //   };

  return (
    <div className="formContainer">
      <form onSubmit={handleSubmit}>
        <h1>ログインフォーム</h1>
        <hr />
        <div className="uiForm">
          <div className="formField">
            <label>ユーザー名</label>
            <input
              type="text"
              name="username"
              placeholder="ユーザー名"
              value={formValues.username}
              onChange={(e) => handleChange(e)}
            />
          </div>
          <p className="errorMsg">{formErros.username}</p>
          <div className="formField">
            <label>パスワード</label>
            <input
              type="text"
              name="password"
              placeholder="パスワード"
              value={formValues.password}
              onChange={(e) => handleChange(e)}
            />
          </div>
          <p className="errorMsg">{formErros.password}</p>
          <button className="submitButton">登録</button>
          {Object.keys(formErros).length === 0 && isSubmit && (
            <div className="msgOk">ログインに成功しました</div>
          )}
        </div>
      </form>
    </div>
  );
};

export default Login;
