import React, { useContext, useRef, useState } from "react";
import { VariableContext } from "../App";
import "./SignUp.css";

const SignUp = () => {
  const [, , , , setSignUpFlag] = useContext(VariableContext);
  const [inputData, setInputData] = useState({
    "user-id": "",
    "first-name": "",
    "last-name": "",
    password: "",
  });
  const htmlRef = useRef<HTMLDivElement>(null);

  const submitInputData = (e: { preventDefault: () => void }) => {
    e.preventDefault();
    const signUpdata = async () => {
      const response = await fetch("http://localhost:3333/signUp", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(inputData),
      })
        .then((e) => e.json())
        .catch(() => false);
      console.log("$$$$$$$$$$$$$", response);
      if (response) {
        setSignUpFlag(false);
        alert("Sign up completed !!");
      } else {
        alert(
          "入力したログインIDは既に使用されています。別のログインIDを入力してください"
        );
      }
    };
    signUpdata();
  };

  return (
    <div className="popUpBack2">
      <div
        className="formContainer2"
        ref={htmlRef}
        onClick={(e) => {
          e.target === htmlRef.current && setSignUpFlag(false);
        }}
      >
        <div className="formBox2">
          <form onSubmit={submitInputData}>
            <h1>SIGN UP</h1>
            <hr />
            <div className="uiForm2">
              <div className="formField2">
                <label>USER ID</label>
                <input
                  type="number"
                  name="user-id"
                  placeholder="User Id (numbers only)"
                  value={inputData["user-id"]}
                  required
                  onChange={(e) =>
                    setInputData({
                      ...inputData,
                      [e.target.name]: e.target.value,
                    })
                  }
                />
              </div>
              <div className="formField2">
                <label>FIRST NAME</label>
                <input
                  type="text"
                  name="first-name"
                  placeholder="First name"
                  value={inputData["first-name"]}
                  required
                  onChange={(e) =>
                    setInputData({
                      ...inputData,
                      [e.target.name]: e.target.value,
                    })
                  }
                />
              </div>
              <div className="formField2">
                <label>LAST NAME</label>
                <input
                  type="text"
                  name="last-name"
                  placeholder="Last name"
                  value={inputData["last-name"]}
                  required
                  onChange={(e) =>
                    setInputData({
                      ...inputData,
                      [e.target.name]: e.target.value,
                    })
                  }
                />
              </div>
              <div className="formField2">
                <label>PASSWORD</label>
                <input
                  type="password"
                  name="password"
                  placeholder="Password"
                  value={inputData.password}
                  required
                  onChange={(e) =>
                    setInputData({
                      ...inputData,
                      [e.target.name]: e.target.value,
                    })
                  }
                />
              </div>
              {/* ボタン */}
              <button className="submitButton2">SUBMIT</button>
            </div>
          </form>
          <button
            className="closeButton"
            onClick={() => {
              setSignUpFlag(false);
            }}
          >
            CLOSE
          </button>
        </div>
      </div>
    </div>
  );
};

export default SignUp;
