import React, { useContext, useEffect, useState } from "react";
import "./Header.css";
import SearchIcon from "@mui/icons-material/Search";
import TuneIcon from "@mui/icons-material/Tune";
import SyncAltIcon from "@mui/icons-material/SyncAlt";
import { VariableContext } from "../App";

export default function Header() {
  const [condition, setCondition] = useState({ fil: "", order: "desc" });
  const [keyWord, setKeyWord] = useState("");
  const [, setPopup, , setPostedArray] = useContext(VariableContext);
  //　あとで消す
  const dummytag = ["react", "dummy", "dummy2"];

  useEffect(() => {
    (async () => {
      const data = await fetch("http://localhost:3333/posted").then((e) =>
        e.json()
      );
      if (condition.fil !== "") {
        const newData = data.filter(
          (e: {
            id: number;
            title: string;
            "post-date": string;
            tag: string;
            url: string;
            pict: string;
            "p-user-id": number;
          }) => e.tag === condition.fil
        );
        condition.order === "asc" && newData.reverse();
        setPostedArray(newData);
      } else {
        condition.order === "asc" && data.reverse();
        setPostedArray(data);
      }
    })();
  }, [condition]);

  //   useEffect(() => {
  //         console.log("キーワード結果：", filterValue);
  //         // タグ
  //         filterValue = filterValue.filter(
  //           (elm) => elm.tag.toLowerCase().includes(tagSave) || tagSave === "All"
  //         );
  //   }, [searchSave, tagSave, sortSave]);

  return (
    <header>
      {/* 検索窓 */}
      <section className="search">
        <input
          type="search"
          placeholder="You can get keyphrase!"
          value={keyWord}
          onChange={(e) => {
            setKeyWord(e.currentTarget.value);
          }}
        />
        <button
          onClick={(e) => {
            console.log(e);
          }}
        >
          <SearchIcon />
        </button>
      </section>

      {/* タグ選択 */}
      <section>
        <label className="filterIcon">
          <TuneIcon />
        </label>
        <select
          id="fil"
          onChange={(e) =>
            setCondition({ ...condition, fil: e.currentTarget.value })
          }
        >
          <option value="">-- select --</option>
          {dummytag.map((e, ind) => (
            <option key={ind} value={e}>
              {e}
            </option>
          ))}
        </select>

        {/* ソート */}
        <label className="orderIcon">
          <SyncAltIcon />
        </label>
        <select
          name="oreder"
          id="order"
          onChange={(e) =>
            setCondition({ ...condition, order: e.currentTarget.value })
          }
        >
          <option value="desc">desc</option>
          <option value="asc">asc</option>
        </select>

        {/* 記事追加 */}
        <button
          onClick={() => {
            setPopup(true);
          }}
        >
          <label>
            <span>➕</span>
          </label>
        </button>
      </section>
    </header>
  );
}
