/* eslint-disable react-hooks/exhaustive-deps */
import React, { useContext, useEffect, useState } from "react";
import "./Header.css";
import SearchIcon from "@mui/icons-material/Search";
import TuneIcon from "@mui/icons-material/Tune";
import SyncAltIcon from "@mui/icons-material/SyncAlt";
import { VariableContext } from "../App";

export default function Header () {
  const [condition, setCondition] = useState({fil: "", order :"desc", keyWord: ""});
  const [, setPopup, , setPostedArray,tagArray] = useContext(VariableContext);

  useEffect(()=>{
    (async () => {
      const fetchData = await fetch("http://localhost:3333/posted").then(e=>e.json());
      const key = new RegExp(condition.keyWord.toLocaleLowerCase());
      const data = fetchData.filter((e:any)=> key.test(e.title.toLocaleLowerCase())||key.test(e.tag.toLocaleLowerCase()))
        if (condition.fil !== "") {
            const newData = data.filter((e:{tag:string}) => e.tag === condition.fil);
            condition.order === "asc" && newData.reverse();
            setPostedArray(newData);
        }else{
            condition.order === "asc" && data.reverse();
            setPostedArray(data);
        }
    })();
  },[condition]);

  return (
    <header>
      {/* 検索窓 */}
      <section className="search">
        <input type="search" placeholder="You can get keyphrase!" value={condition.keyWord} onChange={e=>setCondition({...condition, keyWord: e.currentTarget.value})} />
        <button onClick={(e)=>{}}><SearchIcon /></button>
      </section>

      {/* タグ選択 */}
      <section>
        <label className="filterIcon"><TuneIcon /></label>
        <select id="fil" onChange={e=>setCondition({...condition, fil:e.currentTarget.value})}>
          <option value="" >-- select --</option>
          { tagArray.map((e, ind) => <option key={ind} value={e}>{e}</option>) }
        </select>

      {/* ソート */}
        <label className="orderIcon"><SyncAltIcon /></label>
        <select name="oreder" id="order" onChange={e=>setCondition({...condition,order: e.currentTarget.value})}>
          <option value="desc" >desc</option>
          <option value="asc">asc</option>
        </select>

      {/* 記事追加 */}
        <button onClick={()=>{setPopup(true)}}>
          <label><span>➕</span></label>
        </button>
      </section>
    </header>
  );
};
